var itemnumber = 0
var bigpic = document.getElementById("bigpic");
var timer = '';


function slideleft(){
  itemnumber = itemnumber -1;
  if( itemnumber <0 ){
   itemnumber = mylist.length -1;
   }
  bigpic.src = mylist[itemnumber];
}

function slideright(){
  itemnumber = itemnumber +1;
  if(itemnumber > mylist.length -1){
    itemnumber =0;
   }
      bigpic.src = mylist[itemnumber];
}

function autoplay(){
 window.clearInterval(timer);
timer = setInterval (slideright,2000);
}

function stop(){
window.clearInterval(timer);
}

function randompic(){
var rand = Math.floor( Math.random() * mylist.length);
 itemnumber = rand;
bigpic.src = mylist[itemnumber];
}


var gameon = false;
var time = "";
var highscore = 0;

function clicker() {
  var clicks = document.getElementById("clickcount");
  var timeleft = document.getElementById("timeleft");

  if (gameon) {
    clicks.innerHTML = parseInt(clicks.innerHTML) + 1;
  } else if (timeleft.innerHTML > 0) {
    gameon = true;
    clicks.innerHTML = 0;
    timer = setInterval(countdown, 10);
  }
}

function countdown() {
  var clicks = document.getElementById("clickcount");
  var timeleft = document.getElementById("timeleft");
  var cps = document.getElementById("cps");

  timeleft.innerHTML = (parseFloat(timeleft.innerHTML) - 0.01).toFixed(2);

  if (timeleft.innerHTML <= 0.0) {
    window.clearInterval(timer);
    gameon = false;
    cps.innerHTML = clicks.innerHTML / 10;
    checkhighscore();
  }
}

function resetnow() {
  var clicks = document.getElementById("clickcount");
  var timeleft = document.getElementById("timeleft");

  clicks.innerHTML = 0;
  timeleft.innerHTML = 10;

  window.clearInterval(timer);
  gameon = false;
}

function checkhighscore() {
  var clicks = document.getElementById("clickcount");
  var hsnum = document.getElementById("highscore");

  if (clicks.innerHTML > highscore) {
    highscore = clicks.innerHTML;
    hsnum.innerHTML = highscore;
  }
}
